RENAME TABLE mrl_sales_delivery_item TO mrl_sales_delivery_item_tmp,
mrl_sales_delivery_item_nifi TO mrl_sales_delivery_item,
mrl_sales_delivery_item_tmp TO mrl_sales_delivery_item_nifi;

DROP TABLE IF EXISTS mid_Prod.mrl_sales_delivery_tmp_all;
CREATE TABLE mid_Prod.mrl_sales_delivery_tmp_all AS
WITH row_number_DocEntry AS ( SELECT ROW_NUMBER() OVER (ORDER BY DocEntry ASC) row_number, DocEntry FROM `czechrepublic-290206.mid_Prod.midODLN_DLN1` GROUP BY DocEntry ) SELECT row_number_DocEntry.row_number entity_id, T1.entity_id order_id, T0.NumAtCard increment_id, T0.DocEntry sap_id, T0.DocDate ship_date, T0.U_mage2_receiver receiver, T0.U_mage2_rcvphone receiver_phone, T0.Address2 receiver_address, T0.U_dev_phone02 backup_phone, T0.DocTotal total_amount, CASE WHEN T0.CANCELED = 'Y' AND T0.DocStatus = 'C' THEN 'canceled' WHEN T0.CANCELED = 'N' AND T0.DocStatus = 'C' THEN 'complete' WHEN T0.CANCELED = 'N' AND T0.DocStatus = 'O' THEN 'open' END status, T0.DocDate created_at, T0.UpdateDate updated_at, T0.ItemCode product_sku, T0.Dscription product_name, CAST(T0.PriceAfVAT AS NUMERIC) product_price, CAST(T0.Quantity AS INT64) product_qty, CAST(T0.GTotal AS NUMERIC) product_total FROM `czechrepublic-290206.mid_Prod.midODLN_DLN1` T0 LEFT JOIN row_number_DocEntry ON row_number_DocEntry.DocEntry = T0.DocEntry INNER JOIN `czechrepublic-290206.m2v243.m2_order_enity_id_increment_id` T1 ON T0.NumAtCard = T1.increment_id

RENAME TABLE mrl_sales_delivery TO mrl_sales_delivery_tmp,
